mapper.select:
SELECT dgc.id, dgc.user_id, dgc.app_id, dgc.ref, dgc.commit_date, dgc.commit_sha, dgc.commit_content, dgc.url, da.`name` appName FROM devops_gitlab_commit dgc, devops_application da, devops_project dp WHERE
dgc.app_id IN
#{appId}
1
AND da.is_active = 1 AND dgc.app_id = da.id AND dp.id = #{projectId} AND da.project_id = dp.id AND Date(dgc.commit_date) >= #{startDate} AND Date(dgc.commit_date) <= #{endDate}
